<section class="pageAboutResto">
    <div class="pageAboutResto__container">
        <figure class="pageAboutResto__img">
            <img src="public/images/AboutFacility.webp" alt="buscar imagen" />
        </figure>
        <div class="pageAboutResto__contentLarge">
            <h4 class="homeSubtitle pageAboutResto__subtitle">RESTAURANT</h4>
            <h2 class="homeTitle pageAboutResto__title">
                Get Restaurant Facilities & Many Other More
            </h2>
            <p class="pageAboutResto__text">
                Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
                eiusmod tem por incididunt ut labore et dolore magna aliqua. Ut
                enim ad minim veniam, quis nostrud exercitation ullamco laboris
                nisi ut aliquip.
            </p>
            <button class="button button-variant1">TAKE A TOUR</button>
        </div>
    </div>
</section>
<section class="pageAboutFacility">
    <h4 class="homeSubtitle pageAboutFacility__subtitle">FACILITIES</h4>
    <h2 class="homeTitle pageAboutFacility__title">Core Features</h2>
    <div class="pageAboutFacility__slider">
        <div class="swiperAboutFacility">
            <div class="swiper-wrapper">
                @foreach($facilities as $facility)
                <div class="swiper-slide">
                    <div class="pageAboutFacility__item">
                        <figure class="pageAboutFacility__slider__imgs">
                            <img src="{{$facility['icon']}}" alt="" />
                            <span>{{$facility['number']}}</span>
                        </figure>
                        <h3 class="pageAboutFacility__slider__title">
                            {{$facility['name']}}
                        </h3>
                        <p class="pageAboutFacility__slider__text">
                            Lorem ipsum dolor sit amet, consectetur adipisicing elit,
                            sed do eiusmod tempor incididunt ut labore et dolore magna..
                        </p>
                    </div>
                </div>
                @endforeach
            </div>
            <div class="swiperAboutFacility-pagination pageAboutFacility__slider__dots"></div>
        </div>
    </div>
</section>
<section class="pageAboutCounter">
    <h4 class="homeSubtitle pageAboutCounter__subtitle">COUNTER</h4>
    <h2 class="homeTitle pageAboutCounter__title">Some Fun Facts</h2>
    <div class="pageAboutCounter__itemsContainer">
        @foreach ($counters as $counter)
        <div class="pageAboutCounter__items">
            <img src="{{$counter['img']}}" alt="{{$counter['alt']}}" />
            <div class="pageAboutCounter__items__content">
                <h3 class="pageAboutCounter__items__title">{{$counter['counter']}}</h3>
                <p class="pageAboutCounter__items__text">{{$counter['text']}}</p>
            </div>
            <img src="public/images/factArrowIcon.svg" alt="arrow left icon" />
        </div>
        @endforeach
    </div>
    <div class="pageAboutCounter-swiper pageAboutCounter__slider">
        <div class="swiper-wrapper">
            <div class="swiper-slide">
                <img class="pageAboutCounter__slider__img" src="public/images/funFact1.webp" alt="external beds with a view to the mountains" />
            </div>
            <div class="swiper-slide">
                <img class="pageAboutCounter__slider__img" src="public/images/funFact2.webp" alt="hotel bathtub" />
            </div>
        </div>
        <div class="pageAboutCounter__slider__dots pageAboutCounter-swiper-pagination"></div>
    </div>
</section>
@endsection